mapper.select:
select * 
from daily_excercise
where excerciseDay = #{excerciseDay}
and weightNow = #{weightNow}
and equipmentName = #{equipmentName}
and rfidKey = #{rfidKey}

mapper.update:
update daily_excercise
set countNow = countNow + 1, endTime = NOW()
where excerciseDay = #{excerciseDay}
and weightNow = #{weightNow}
and equipmentName = #{equipmentName}
and rfidKey = #{rfidKey}

mapper.select:
SELECT d.equipmentName, weightNow, countNow, ROUND(SUM((6 * (3.5 * u.weight * (ROUND((HOUR(TIMEDIFF(d.endtime, d.startTime)) * 60 )+ (second(TIMEDIFF(d.endtime, d.startTime)) + MINUTE(TIMEDIFF(d.endtime, d.startTime)) * 60) / 60)))) / 1000 * 5)) AS "calorie", date_format(TIMEDIFF(endTime, startTime), '%i:%s') AS exTime, f.equipmentStimulate, f.equipmentEnglishStimulate, u.gender FROM daily_excercise d JOIN fitness_equipment f ON d.equipmentName = f.equipmentName JOIN user u on u.rfidKey = d.rfidKey WHERE d.weightNow = #{weightNow} AND d.rfidKey = #{rfidKey} AND d.equipmentName=#{equipmentName} AND d.excerciseDay = DATE(NOW());
SELECT ROW_NUMBER() OVER(ORDER BY endTime DESC) as id, equipmentName, weightNow, countNow FROM daily_excercise WHERE rfidKey = #{rfidKey} AND excerciseDay = DATE(NOW()) ORDER BY endTime DESC;
SELECT equipmentName, isStarted FROM fitness_equipment WHERE equipmentName = #{equipmentName};
